import React, { useState } from 'react';
import { useNavigate, Outlet } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { deleteTask } from '../TaskController/TaskControllerSlice';
import './showTask.css';

const ShowTask = () => {
  const [activeButton, setActiveButton] = useState('button1');
  const [selectedRow, setSelectedRow] = useState(null); // State for the selected row

  const tasks = useSelector((state) => state.taskController.tasks);//for check

  const navigate = useNavigate(); // Hook for navigation
  const dispatch = useDispatch();

  // Handle button clicks
  const handleButtonClick = (buttonId) => {
    setActiveButton(buttonId);
    if (buttonId === 'button1') {
      navigate('show-individual-tasks'); // Navigate to ShowIndividualTasks
    } else if (buttonId === 'button2') {
      navigate('show-corporate-tasks'); // Navigate to ShowCorporateTasks
    }
  };

  // Handle row selection.We will pass it to the child component through the Outlet context. 
  const handleRowSelect = (rowID) => {
    setSelectedRow(rowID);
  };

  // Handle edit button click
  const handleEditClick = () => {
    if (selectedRow) {
      console.log('Editing row:', selectedRow);
      // navigate(`/edit-task/${selectedRow.id}`);
    }
  };

  // Handle delete button click
  const handleDeleteClick = () => {
    if (selectedRow) {
      console.log('Deleting row:', selectedRow);
      dispatch(deleteTask(selectedRow.taskID)); // Assuming you have a deleteTask action
      setSelectedRow(null); // Reset selected row after deletion
      console.log("",tasks);
    }
  };

  return (
    <div className="show-task-container">
      <div className="button-panel">
        <button
          className={`button ${activeButton === 'button1' ? 'active' : ''}`}
          onClick={() => handleButtonClick('button1')}
        >
          Show individual tasks
        </button>
        <button
          className={`button ${activeButton === 'button2' ? 'active' : ''}`}
          onClick={() => handleButtonClick('button2')}
        >
          Show corporate tasks
        </button>
        <button
          className={`button button-edit ${selectedRow ? 'active' : 'disabled'}`}
          onClick={handleEditClick}
          disabled={!selectedRow}
        >
          Edit
        </button>
        <button
          className={`button button-delete ${selectedRow ? 'active' : 'disabled'}`}
          onClick={handleDeleteClick}
          disabled={!selectedRow}
        >
          Delete
        </button>
      </div>
      <div className="content">
        <Outlet context={{ onRowSelect: handleRowSelect }} /> {/* Render child component with context */}
        {/* if we are using props
        <Outlet />
        <ShowIndividualTasks onRowSelect={handleRowSelect} /> */}
      </div>
    </div>
  );
};

export default ShowTask;
