// src/components/TaskController/TaskController.js
import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { addTask, removeTask, updateTask, filterTasks, sortTasks } from '../../features/taskController/taskControllerSlice';

const TaskController = () => {
  const tasks = useSelector((state) => state.taskController.tasks);
  const dispatch = useDispatch();

  const handleAddTask = (task) => {
    dispatch(addTask(task));
  };

  const handleRemoveTask = (taskId) => {
    dispatch(removeTask(taskId));
  };

  const handleUpdateTask = (task) => {
    dispatch(updateTask(task));
  };

  const handleFilterTasks = (criteria) => {
    dispatch(filterTasks(criteria));
  };

  const handleSortTasks = (criteria) => {
    dispatch(sortTasks(criteria));
  };

  return (
    <div>
      <h2>Task Controller</h2>
      <button onClick={() => handleAddTask(newTask)}>Add Task</button>
      <ul>
        {tasks.map((task) => (
          <li key={task.taskID}>
            {task.taskName} - {task.taskDescription}
            <button onClick={() => handleRemoveTask(task.taskID)}>Remove</button>
            <button onClick={() => handleUpdateTask({ ...task, taskName: 'Updated Task' })}>Update</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TaskController;
