import React from 'react';
import { useSelector } from 'react-redux';

const ShowTask = () => {
  const taskName = useSelector((state) => state.oneTask.taskName);
  const taskDescription = useSelector((state) => state.oneTask.taskDescription);
  const taskID = useSelector((state) => state.oneTask.taskID);
  const taskType = useSelector((state) => state.oneTask.taskType);
  const taskPriority = useSelector((state) => state.oneTask.taskPriority);
  const taskDate = useSelector((state) => state.oneTask.taskDate);
  const taskCompleted = useSelector((state) => state.oneTask.taskCompleted);

  // Виведення стану в консоль
  // const obj =  useSelector((state) => state.oneTask);
  // console.log(obj);

  return (
    <div>
      <h2>Tasks</h2>
      <ul>
        <li>Task name: {taskName}</li>
        <li>Task description: {taskDescription}</li>
        <li>TaskID: {taskID}</li>
        <li>TaskType: {`${taskType.textType}  ${taskType.numberType}`}</li>
        <li>TaskPriority: {`${taskPriority.textPriority} ${taskPriority.numberPriority}`}</li>
        <li>TaskDate: {`${taskDate.startDate} ${taskDate.endDate}`}</li>
        <li>TaskCompleted: {taskCompleted}</li>
      </ul>
    </div>
  );
};

export default ShowTask;
