import React, { useState } from 'react';
import { useNavigate, Outlet } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { deleteTask, filterTasks } from '../TaskController/TaskControllerSlice';
import FilterPanel from '../FilterPanel/FilterPanel'; // Імпорт вашого нового компонента
import './ShowTask.css';

const ShowTask = () => {
  const [activeButton, setActiveButton] = useState('button1');
  const [selectedRow, setSelectedRow] = useState(null);

  const tasks = useSelector((state) => state.taskController.tasks);
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const handleButtonClick = (buttonId) => {
    setActiveButton(buttonId);
    if (buttonId === 'button1') {
      navigate('show-individual-tasks');
    } else if (buttonId === 'button2') {
      navigate('show-corporate-tasks');
    }
  };

  const handleRowSelect = (rowID) => {
    setSelectedRow(rowID);
  };

  const handleEditClick = () => {
    if (selectedRow) {
      console.log('Editing row:', selectedRow);
    }
  };

  const handleDeleteClick = () => {
    if (selectedRow) {
      console.log('Deleting row:', selectedRow);
      dispatch(deleteTask(selectedRow.taskID));
      setSelectedRow(null);
    }
  };

  // Функції для фільтрації
  const handleSearch = (query) => {
    dispatch(filterTasks(q))
    console.log('Search query:', query);

  };

  const handleTypeFilterChange = (value) => {
    console.log('Type filter value:', value);
    // Додайте тут логіку для фільтрації за типом
  };

  const handleSortChange = (value) => {
    console.log('Sort by:', value);
    // Додайте тут логіку для сортування
  };

  return (
    <div className="show-task-container">
      <div className="button-panel">
        <button
          className={`button ${activeButton === 'button1' ? 'active' : ''}`}
          onClick={() => handleButtonClick('button1')}
        >
          Show individual tasks
        </button>
        <button
          className={`button ${activeButton === 'button2' ? 'active' : ''}`}
          onClick={() => handleButtonClick('button2')}
        >
          Show corporate tasks
        </button>
        <button
          className={`button button-edit ${selectedRow ? 'active' : 'disabled'}`}
          onClick={handleEditClick}
          disabled={!selectedRow}
        >
          Edit
        </button>
        <button
          className={`button button-delete ${selectedRow ? 'active' : 'disabled'}`}
          onClick={handleDeleteClick}
          disabled={!selectedRow}
        >
          Delete
        </button>
      </div>
      <div className="filter-panel-container">
        <FilterPanel
          onSearch={handleSearch}
          onTypeFilterChange={handleTypeFilterChange}
          onSortChange={handleSortChange}
        />
      </div>
      <div className="content">
        <Outlet context={{ onRowSelect: handleRowSelect }} />
      </div>
    </div>
  );
};

export default ShowTask;
