import React from 'react';
import { useSelector } from 'react-redux';
import './ShowIndividualTask.css'; // Стилі для таблиці

const ShowIndividualTasks = () => {
  // Вибірка даних з Redux
  const tasks = useSelector((state) => state.taskController.tasks);

  return (
    <div className="tasks-table">
      <h2>Individual Tasks</h2>
      <table>
        <thead>
          <tr>
            <th>Task ID</th>
            <th>Task Name</th>
            <th>Description</th>
            <th>Type</th>
            <th>Priority</th>
            <th>Start Date</th>
            <th>End Date</th>
            <th>Completed</th>
          </tr>
        </thead>
        <tbody>
          {tasks.map((task) => (
            <tr key={task.taskID}>
              <td>{task.taskID}</td>
              <td>{task.taskName}</td>
              <td>{task.taskDescription}</td>
              <td>{task.taskType.textType}</td>
              <td>{task.taskPriority.textPriority}</td>
              <td>{task.taskDate.startDate}</td>
              <td>{task.taskDate.endDate}</td>
              <td>{task.taskCompleted ? 'Yes' : 'No'}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ShowIndividualTasks;
