import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { createName, createDescription, createID, createTaskType, createPriority, createDate,createCompleted } from './OneTaskSlice';
import TypeTaskSelect from './TypeTaskSelect/TypeTaskSelect';
import PrioritySelect from './PrioritySelect/PrioritySelect';
import DateRangeSelect from './DateRangeSelect/DateRangeSelect';

import { addTask } from '../TaskController/TaskControllerSlice';

import './oneTask.css'

const OneTask = () => {

  const generateShortID = () => {
    const uuid = uuid4(); // Генеруємо UUID
    return uuid.slice(0, 5); // Використовуємо перші 5 символів UUID як короткий ID
}

  const [taskName, setTaskName] = useState('');
  const [taskDescription, setTaskDescription] = useState('');
  const [taskType, setTaskType] = useState({ numberType: 0, textType: "" });
  const [taskPriority, setTaskPriority] = useState({ numberPriority: 0, textPriority: "" })
  const [taskDate, setTaskDate] = useState({ startDate: '', endDate: '' });
  const [taskCompleted, setTaskCompleted] = useState(false);

  const dispatch = useDispatch();

  const handleTaskNameChange = (e) => {
    setTaskName(e.target.value);
  };

  const handleTaskDescriptionChange = (e) => {
    setTaskDescription(e.target.value);
  };

  const handleTaskTypeChange = (typeObj) => {
    setTaskType(typeObj);
  };

  const handleTaskPriorityChange = (priorityObj) => {
    setTaskPriority(priorityObj);
  }

  const handleTaskDate = (taskDateObj) => {
    setTaskDate(taskDateObj);
  }

  const handleSave = () => {
    if (taskName.trim() && taskDescription.trim()) {

      // dispatch(createName(taskName));
      // dispatch(createDescription(taskDescription));
      // dispatch(createID());
      // dispatch(createTaskType(taskType));
      // dispatch(createPriority(taskPriority));
      // dispatch(createDate(taskDate));
      // dispatch(createCompleted(taskCompleted));

      const newTask = {
        taskID:dispatch(createID()),
        taskName,
        taskDescription,
        taskType,
        taskPriority,
        taskDate,
        taskCompleted
      }

      setTaskName("");
      setTaskDescription("");
      setTaskType({ numberType: 0, textType: "" });
      setTaskPriority({ numberPriority: 0, textPriority: "" });
      setTaskDate({ startDate: "", endDate: "" });
      setTaskCompleted(false);
    }
  };

  return (
    <section className="task__form">
      <form className='form__one__task' onSubmit={(e) => { e.preventDefault(); handleSave() }}>

        <input
          className="task__name"
          value={taskName}
          onChange={handleTaskNameChange}
          placeholder="Enter task name"
        />

        <textarea
          className="task__description"
          value={taskDescription}
          onChange={handleTaskDescriptionChange}
          placeholder="Enter task description"
        />

        <div className='type__priority__select'>
          <TypeTaskSelect value={taskType} onChange={handleTaskTypeChange} />
          <PrioritySelect value={taskPriority} onChange={handleTaskPriorityChange} />
        </div>

        <DateRangeSelect value={taskDate} onChange={handleTaskDate} />

        <button className="save__task" type='submit'>Save</button>

      </form>
    </section>
  );
};

export default OneTask;
