import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { updateTask } from '../TaskController/TaskControllerSlice';
import TypeTaskSelect from './TypeTaskSelect/TypeTaskSelect';
import PrioritySelect from './PrioritySelect/PrioritySelect';
import DateRangeSelect from './DateRangeSelect/DateRangeSelect';
// import { generateShortID } from './OneTaskSlice';
import './oneTask.css';

const OneTask = ({ task, onSave }) => {
  const [taskName, setTaskName] = useState('');
  const [taskDescription, setTaskDescription] = useState('');
  const [taskType, setTaskType] = useState({ numberType: 0, textType: '' });
  const [taskPriority, setTaskPriority] = useState({ numberPriority: 0, textPriority: '' });
  const [taskDate, setTaskDate] = useState({ startDate: '', endDate: '' });
  const [taskCompleted, setTaskCompleted] = useState(false);

  const dispatch = useDispatch();

  useEffect(() => {
    if (task) {
      setTaskName(task.taskName);
      setTaskDescription(task.taskDescription);
      setTaskType(task.taskType);
      setTaskPriority(task.taskPriority);
      setTaskDate(task.taskDate);
      setTaskCompleted(task.taskCompleted);
    }
  }, [task]);

  const handleSave = () => {
    if (taskName.trim() && taskDescription.trim()) {
      const updatedTask = {
        ...task,
        taskName,
        taskDescription,
        taskType,
        taskPriority,
        taskDate,
        taskCompleted,
      };

      dispatch(updateTask(updatedTask));
      onSave();
    }
    alert("Task is updated");
  };

  return (
    <section className="task__form">
      <form className='form__one__task' onSubmit={(e) => { e.preventDefault(); handleSave(); }}>
        <input
          className="task__name"
          value={taskName}
          onChange={(e) => setTaskName(e.target.value)}
          placeholder="Enter task name"
        />
        <textarea
          className="task__description"
          value={taskDescription}
          onChange={(e) => setTaskDescription(e.target.value)}
          placeholder="Enter task description"
        />
        <div className='type__priority__select'>
          <TypeTaskSelect value={taskType} onChange={setTaskType} />
          <PrioritySelect value={taskPriority} onChange={setTaskPriority} />
        </div>
        <DateRangeSelect value={taskDate} onChange={setTaskDate} />
        <div className='completed-div'>
          <label className='completed'>
            Completed
            <input
              type="checkbox"
              checked={taskCompleted}
              onChange={(e) => setTaskCompleted(e.target.checked)}
            />
          </label>
        </div>
        <button className="save__task" type='submit'>Save</button>
      </form>
    </section>
  );
};

export default OneTask;
